import './App.css';
import logo from './logo.svg';
//these below are all the same
import React from 'react';
// var { Component } = React;
// import React, { component } from 'react';
import Coin from'./components/Coin/Coin';
import AccountBalance from'./components/AccountBalance/AccountBalance';
import styled from 'styled-components'

const Table = styled.table`
  margin: 25px auto 25px auto;
  display: inline-block;
  padding: 5px;
`;

class App extends React.Component {
  constructor(props) {
    super(props);
      this.state = {
        balance: 10000,
        coinData: [
          {
            name: 'Bitcoin',
            ticker: 'BTC',
            price: 9999.99
          },
          {
            name: 'Ethereum',
            ticker: 'ETH',
            price: 299.99
          },
          {
            name: 'Tether',
            ticker: 'USDT',
            price: 1.0
          },
          {
            name: 'Ripple',
            ticker: 'XRP',
            price: 0.2
          },
          {
            name: 'Bitcoin Cash',
            ticker: 'BCH',
            price: 298.99
          },

        ]
      }
  }
    render() {
      return (
        <div className="App">
          <header className="App-header">
            <img src={logo} alt="React Logo" className="App-logo" />
            <h2 className="App-title">Coin Exchange</h2>
          </header>
          <AccountBalance amount={this.state.balance} />
        <Table className>
          <thead>
            <tr>
              <th>Name</th>
              <th>Ticker</th>
              <th>Price</th>
            </tr>
          </thead>

          <tbody>
            {
              //if you run this, you will receive the
                //"each child in a list should have a unique key prop error"
                // so you can either use the ticker value since that should be unique
                // or you can use uuidv4 or uuid
                // then every object item would need the uuid

                this.state.coinData.map( value =>
                  <Coin name={value.name} ticker={value.ticker} price={value.price} />
                )
            }

            {
              //then, there is also an alternative way from doing the above and shorter
              //this.state.coinData.map( value =>
              //  <Coin key={value.ticker} {...value} />
              //)
            }

          </tbody>
        </Table>
        </div>
      );
    }
}

export default App;
