import React, { Component } from 'react'
import PropTypes from 'prop-types';
import './AccountBalance.css';

export default class AccountBalance extends Component {
    constructor(props) {
    super(props);
        this.handleToggle = this.handleToggle.bind(this);

        this.state = {hidden: true}
        
    }

    handleToggle(event) {
        event.preventDefault();
        this.setState( {hidden: !this.state.hidden} );

    }


    render() {
            const buttonText = this.props.showBalance ? 'Hide Balance' : 'Show Balance';

        return (
            <section className="AccountBalance">
                
                <form action="#" method="POST">
                <div className="BalanceToggle" hidden={this.state.hidden}>
                    Account Balance: ${this.props.amount}
                
                    <button className="BalanceButton" hidden={false} 
                            onClick={this.handleToggle}> {buttonText} </button>
                </div>
                </form>
            
            </section>
        );
    }
}

AccountBalance.propTypes = {
    amount: PropTypes.number.isRequired
}
