import './App.css';
import logo from './logo.svg';
//these below are all the same
import React from 'react';
// var { Component } = React;
// import React, { component } from 'react';
import Coin from'./components/Coin/Coin';
import AccountBalance from'./components/AccountBalance/AccountBalance';
import styled from 'styled-components'

const Table = styled.table`
  margin: 25px auto 25px auto;
  display: inline-block;
  padding: 5px;
`;

class App extends React.Component {
  constructor() {
    super(props);
      this.state = {
        balance: 10000,
        coinData: [
          {
            name: 'Bitcoin',
            ticker: 'BTC',
            price: 9999.99
          },
          {
            name: 'Ethereum',
            ticker: 'ETH',
            price: 299.99
          },
          {
            name: 'Tether',
            ticker: 'USDT',
            price: 1.0
          },
          {
            name: 'Ripple',
            ticker: 'XRP',
            price: 0.2
          },

        ]
      }
  }
    render() {
      return (
        <div className="App">
          <header className="App-header">
            <img src={logo} alt="React Logo" className="App-logo" />
            <h2 className="App-title">Coin Exchange</h2>
          </header>
          <AccountBalance amount={this.state.balance} />
        <Table className>
          <thead>
            <tr>
              <th>Name</th>
              <th>Ticker</th>
              <th>Price</th>
            </tr>
          </thead>

          <tbody>
            <Coin name={this.state.coinData[0].name} 
                  ticker={this.state.coinData[0].ticker} 
                  price={this.state.coinData[0].price} />
            <Coin name={this.state.coinData[1].name} 
                  ticker={this.state.coinData[1].ticker} 
                  price={this.state.coinData[1].price} />
            <Coin name={this.state.coinData[2].name} 
                  ticker={this.state.coinData[2].ticker} 
                  price={this.state.coinData[2].price} />
            <Coin name={this.state.coinData[3].name} 
                  ticker={this.state.coinData[3].ticker} 
                  price={this.state.coinData[3].price} />
          </tbody>
        </Table>
        </div>
      );
    }
}

export default App;
